                                AutoSkip = false,
                                Min = 0 // Otherwise the lowest value in the dataset won't be visible
                            }
                        }
                    }
                }
            }
        };

        _barChartConfig.Data.Labels.AddRange(model.MonthNames());

        //Note the constructor argument
        _barDataSet = new BarDataset<FlightModel>()
        {
            Label = "My double dataset",
            BackgroundColor = new[] { ColorUtil.RandomColorString(), ColorUtil.RandomColorString(), ColorUtil.RandomColorString(), ColorUtil.RandomColorString() },
            BorderColor = ColorUtil.RandomColorString(),
            BorderWidth = 1
        };

        _barChartConfig.Data.Datasets.Add(_barDataSet);
    }

    private async Task AddData()
    {
        int index = 0;
        foreach(var flight in flights)
        {
            _barChartConfig.Data.Labels.Add(flights[index].MonthName);
            _barDataSet.Add(flights[index]);
            _barDataSet.BackgroundColor = _barChartConfig.Data.Labels.Select(l => ColorUtil.RandomColorString()).ToArray();

            _barChartConfig.Options.Title.Text += flights[index].MonthName;

            await _barChart.Update();
            index++;
        }
        
    }


